public static String getDataDir(MiniDFSCluster dfsCluster, String dataDir)
    throws IOException {
  URI uri = dfsCluster.getURI();
  String dir = uri.toString()
      + "/"
      + new File(dataDir).toString().replaceAll(":", "_")
          .replaceAll("/", "_");
  return dir;
}

