private boolean isValidEnEnding(StringBuffer sb, int index) {
  char c = sb.charAt(index);
  if (isVowel(c))
    return false;
  if (c < 3)
    return false;
  // ends with "gem"?
  if (c == 'm' && sb.charAt(index - 2) == 'g' && sb.charAt(index - 1) == 'e')
    return false;
  return true;
}

