        return mpq;
      }
    }
    else {
      PhraseQuery pq = newPhraseQuery();
      pq.setSlop(phraseSlop);
      int position = -1;


      for (int i = 0; i < numTokens; i++) {
        BytesRef term = new BytesRef();
        int positionIncrement = 1;

        try {
          boolean hasNext = buffer.incrementToken();
          assert hasNext == true;
