public static Object newInstance(String cname, String... subpackages) {
  Class clazz = findClass(cname,subpackages);
  try {
    return clazz.newInstance();
  } catch (Exception e) {
    throw new SolrException(500,"Error instantiating class " + clazz, e, false);
  }
}

