@Override
public int advance(int target) throws IOException {
  if (target < valueCount) {
    final long address = idx.advance(target);
    pos = idx.ord();
    if(address == 0) {
      // default is empty
      ref.length = 0;
      ref.offset = 0;
      return pos;
    }
    fill(address, ref);
    return pos;
  }
  return pos = NO_MORE_DOCS;
}

@Override
public int advance(int target) throws IOException {
  if (target < valueCount) {
    long address;
    while ((address = idx.advance(target)) == 0) {
      if (++target >= valueCount) {
        return pos = NO_MORE_DOCS;
      }
    }
    pos = idx.ord();
    fill(address, ref);
    return pos;
  }
  return pos = NO_MORE_DOCS;
}

