public static boolean recurseDelete(File f) {
  if (f.isDirectory()) {
    for (File sub : f.listFiles()) {
      if (!recurseDelete(sub)) {
        System.err.println("!!!! WARNING: best effort to remove " + sub.getAbsolutePath() + " FAILED !!!!!");
        return false;
      }
    }
  }
  return f.delete();
}

