  final int[] freqs = new int[bufferSize];

  int buffered = 0;
  int doc;
  while ((doc = nextDoc()) != NO_MORE_DOCS) {
    docIDs[buffered] = doc;
    scores[buffered] = score();
    freqs[buffered] = freq();

    if (++buffered == bufferSize) {
      shuffle(docIDs, scores, freqs, buffered);
      flush(docIDs, scores, freqs, buffered, fake, collector);
      buffered = 0;
