 */
public boolean accept(File dir, String name) {
  if (name.lastIndexOf('.') != -1) {
    // Has an extension
    return luceneFilePattern.matcher(name).matches();
  } else {
    // No extension -- only segments_N file;
    return name.startsWith(IndexFileNames.SEGMENTS);
  }
}

