public final static void writeXML(Writer out, String tag, String val) throws IOException {
  out.write('<');
  out.write(tag);
  if (val == null) {
    out.write("/>");
  } else {
    out.write('>');
    escapeCharData(val,out);
    out.write("</");
    out.write(tag);
    out.write('>');
  }
}

