    size += (numBlocks - 1) * bytesUsedPerBlock;
    size += RamUsageEstimator.sizeOf(blocks[numBlocks - 1]);
  }
  if (currentBlock != null) {
    size += RamUsageEstimator.sizeOf(currentBlock);
  }
  return size;
}

