@Override
public void inform(ResourceLoader loader) throws SolrException {
  if(loader == null) {
    throw new SolrException(ErrorCode.BAD_REQUEST, "Needs ResourceLoader in order to load config file");
  }
  this.loader = loader;
  reload();
}

