commits.sourceNew:
/** 1<<blockBits must be bigger than biggest single
 *  BytesRef slice that will be pulled */
public PagedBytes(int blockBits) {
  this.blockSize = 1 << blockBits;
  this.blockBits = blockBits;
  blockMask = blockSize-1;
  upto = blockSize;
}

