private DocIdSetIterator getDISI(Filter filter, ReaderContext info)
throws IOException {
    DocIdSet docIdSet = filter.getDocIdSet(info);
    if (docIdSet == null) {
      return DocIdSet.EMPTY_DOCIDSET.iterator();
    } else {
      DocIdSetIterator iter = docIdSet.iterator();
      if (iter == null) {
        return DocIdSet.EMPTY_DOCIDSET.iterator();
      } else {
        return iter;
      }
    }
}

