@Override
public Query rewrite(IndexReader reader) throws IOException {
  Query newQ = q.rewrite(reader);
  if (newQ != q) {
    FilterQuery fq = new FilterQuery(newQ);
    fq.setBoost(this.getBoost());
    return fq;
  } else {
    return this;
  }
}

