          System.out.println("-" + MAKEPATH + " requires one arg - the path to make");
          System.exit(1);
        }
        zkClient.makePath(arglist.get(0).toString(), true);
      } else if (line.getOptionValue(CMD).equals(PUT)) {
        List arglist = line.getArgList();
