          System.out.println("-" + PUT + " requires two args - the path to create and the data string");
          System.exit(1);
        }
        zkClient.create(arglist.get(0).toString(), arglist.get(1).toString().getBytes(StandardCharsets.UTF_8), CreateMode.PERSISTENT, true);
