      System.out.println("zkcli.sh -zkhost localhost:9983 -cmd " + LIST);
      System.out.println("zkcli.sh -zkhost localhost:9983 -cmd " + CLUSTERPROP + " -" + NAME + " urlScheme -" + VALUE_LONG + " https" );
