    String zkServerAddress = line.getOptionValue(ZKHOST);
    String solrHome = line.getOptionValue(SOLRHOME);

    String solrPort = null;
    if (line.hasOption(RUNZK)) {
      if (!line.hasOption(SOLRHOME)) {
