        // up in the first place...

      } else if (line.getOptionValue(CMD).equalsIgnoreCase(UPCONFIG)) {
        if (!line.hasOption(CONFDIR) || !line.hasOption(CONFNAME)) {
          stdout.println("-" + CONFDIR + " and -" + CONFNAME
              + " are required for " + UPCONFIG);
          System.exit(1);
        }
