@Override
protected float score(DisiWrapper<Scorer> topList) throws IOException {
  double score = 0;
  int freq = 0;
  for (DisiWrapper<Scorer> w = topList; w != null; w = w.next) {
    score += w.iterator.score();
    freq += 1;
  }
  return (float)score * coord[freq];
}

