 */
@Test
public void testDistanceOrder() throws IOException {
  adoc("100","1,2");
  adoc("101","4,-1");
  commit();

  //query closer to #100
  checkHitsOrdered("Intersects(Circle(3,4 d=1000))", "101", "100");
  //query closer to #101
  checkHitsOrdered("Intersects(Circle(4,0 d=1000))", "100", "101");
}

