/**
 * Marshals a string-based field value.
 */
protected static Object marshalStringSortValue(Object value) {
  if (null == value) {
    return null;
  }
  CharsRef spare = new CharsRef();
  UnicodeUtil.UTF8toUTF16((BytesRef)value, spare);
  return spare.toString();
}

