@Override
public Map<String, Object> getNodeValues(String node, Collection<String> keys) {
  AutoScalingSnitch  snitch = new AutoScalingSnitch();
  ClientSnitchCtx ctx = new ClientSnitchCtx(null, node, snitchSession, solrClient);
