  String algLines[] = {
      "content.source=org.apache.lucene.benchmark.byTask.feeds.LineDocSource",
      "docs.file=" + getReuters20LinesFile(),
      "content.source.forever=false",
      "directory=RAMDirectory",
      "NewShingleAnalyzer(" + params + ")",
      "CreateIndex",
      "{ \"AddDocs\"  AddDoc > : * "
  };
  return algLines;
}

