void tryPurge(IOUtils.IOConsumer<FlushTicket> consumer) throws IOException {
  assert !Thread.holdsLock(this);
  if (purgeLock.tryLock()) {
    try {
      innerPurge(consumer);
    } finally {
      purgeLock.unlock();
    }
  }
}

