static void shutdownExecutorService(ExecutorService ex) {
  if (ex != null) {
    ex.shutdown();
    try {
      ex.awaitTermination(1000, TimeUnit.MILLISECONDS);
    } catch (InterruptedException e) {
      e.printStackTrace();
    }
  }
}

