public static void getAllFiles(File srcDir, FileFilter filter, List allFiles)
{
    File [] files = srcDir.listFiles(filter);
    for (int i = 0; i < files.length; i++)
    {
        File file = files[i];
        if (file.isDirectory())
        {
            getAllFiles(file, filter, allFiles);
        }
        else
        {
            allFiles.add(file);
        }
    }
}

