/** Quantizes double (64 bit) longitude into 32 bits */
public static int encodeLon(double lon) {
  assert GeoUtils.isValidLon(lon): "lon=" + lon;
  long x = (long) (lon * LON_SCALE);
  assert x < Integer.MAX_VALUE;
  assert x > Integer.MIN_VALUE;
  return (int) x;
}

