@Override
public int nextDoc() {
  while (posPending > 0) {
    nextPosition();
  }
  while(true) {
    //System.out.println("  nextDoc cycle docUpto=" + docUpto + " numDocs=" + numDocs + " fp=" + in.getPosition() + " this=" + this);
    if (docUpto == numDocs) {
      //System.out.println("    END");
      return docID = NO_MORE_DOCS;
    }
    docUpto++;

    final int code = in.readVInt();
    accum += code >>> 1;
    if ((code & 1) != 0) {
      freq = 1;
    } else {
      freq = in.readVInt();
      assert freq > 0;
    }

    if (liveDocs == null || liveDocs.get(accum)) {
      pos = 0;
      startOffset = storeOffsets ? 0 : -1;
      posPending = freq;
      //System.out.println("    return docID=" + accum + " freq=" + freq);
      return (docID = accum);
    }

    // Skip positions
    for(int posUpto=0;posUpto<freq;posUpto++) {
      if (!storePayloads) {
        in.readVInt();
      } else {
        final int skipCode = in.readVInt();
        if ((skipCode & 1) != 0) {
          payloadLength = in.readVInt();
          //System.out.println("    new payloadLen=" + payloadLength);
        }
      }

      if (storeOffsets) {
        if ((in.readVInt() & 1) != 0) {
          // new offset length
          offsetLength = in.readVInt();
        }
      }

      if (storePayloads) {
        in.skipBytes(payloadLength);
      }
    }
  }
}

