// LUCENE-1196
public void testIllegalEOF() throws Exception {
  RAMDirectory dir = new RAMDirectory();
  IndexOutput o = dir.createOutput("out");
  byte[] b = new byte[1024];
  o.writeBytes(b, 0, 1024);
  o.close();
  IndexInput i = dir.openInput("out");
  i.seek(1024);
  i.close();
  dir.close();
}

