@Override
public void reset(Reader input) throws IOException {
  super.reset(input);
  cachedStates.clear();
  stringValue = null;
  binaryValue = null;
  ParseResult res = parser.parse(input, this);
  if (res != null) {
    stringValue = res.str;
    binaryValue = res.bin;
    if (res.states != null) {
      cachedStates.addAll(res.states);
    }
  }
}

