public void copyInts(IntsRef other) {
  if (ints == null) {
    ints = new int[other.length];
  } else {
    ints = ArrayUtil.grow(ints, other.length);
  }
  System.arraycopy(other.ints, other.offset, ints, 0, other.length);
  length = other.length;
  offset = 0;
}

