@Override
public boolean fill(final CharacterBuffer buffer, final Reader reader) throws IOException {
  buffer.offset = 0;
  final int read = reader.read(buffer.buffer);
  if(read == -1)
    return false;
  buffer.length = read;
  return true;
}

