@Override
public int doLogic() throws Exception {
  IndexWriter writer = getRunData().getIndexWriter();
  if (useAddIndexesDir) {
    writer.addIndexes(inputDir);
  } else {
    IndexReader r = DirectoryReader.open(inputDir);
    try {
      writer.addIndexes(r);
    } finally {
      r.close();
    }
  }
  return 1;
}

