public SortedMap<K, V> getReadCopy() {
  SortedMap<K, V> m = copy;
  if (m != null) {
    return m;
  }

  // we have to clone
  cloneLock.lock();
  try {
    // check again - maybe a different thread was faster
    m = copy;
    if (m != null) {
      return m;
    }

    // still no copy there - create one now
    SortedMap<K, V> clone = clone(delegate);
    copy = clone;
    return clone;
  } finally {
    cloneLock.unlock();
  }

}

