public void testJoin() {
  assertEquals("a|b|c",   StrUtils.join(Arrays.asList("a","b","c"), '|'));
  assertEquals("a,b,c",   StrUtils.join(Arrays.asList("a","b","c"), ','));
  assertEquals("a\\,b,c", StrUtils.join(Arrays.asList("a,b","c"), ','));
  assertEquals("a,b|c",   StrUtils.join(Arrays.asList("a,b","c"), '|'));

  assertEquals("a\\\\b|c",   StrUtils.join(Arrays.asList("a\\b","c"), '|'));
}

