/** Merge run i with run i+1 */
void mergeAt(int i) {
  assert stackSize > i + 1;
  final int l = runBase(i+1);
  final int pivot = runBase(i);
  final int h = runEnd(i);
  merge(l, pivot, h, pivot - l, h - pivot);
  for (int j = 1; j <= i+1; ++j) {
    setRunEnd(j, runEnd(j-1));
  }
  --stackSize;
}

/** Merge run i with run i+1 */
void mergeAt(int i) {
  assert stackSize > i + 1;
  final int l = runBase(i+1);
  final int pivot = runBase(i);
  final int h = runEnd(i);
  merge(l, pivot, h, pivot - l, h - pivot);
  for (int j = i + 1; j > 0; --j) {
    setRunEnd(j, runEnd(j-1));
  }
  --stackSize;
}

