    static void makeContainer(String fn) {
		//new File(fn).mkdirs();
	    int n = fn.lastIndexOf(File.separatorChar);
	    if (n < 0) {
	    	n = fn.lastIndexOf('/');
	        fn = fn.replace('/', File.separatorChar); // need?
	    }
	    if (n >= 0)
    	    new File(fn.substring(0, n)).mkdirs();
    }

