@Override
public int read(char cbuf[], int off, int len) throws IOException {
  int i = 0;
  for ( ; i < len ; ++i) {
    int ch = read();
    if (ch == -1) break;
    cbuf[off++] = (char)ch;
  }
  return i > 0 ? i : (len == 0 ? 0 : -1);
}

