// convenience method
public static TokenStream getTokenStream(String field, String contents,
    Analyzer analyzer) {
  try {
    return analyzer.tokenStream(field, new StringReader(contents));
  } catch (IOException ex) {
    throw new RuntimeException(ex);
  }
}

