/** add a field for facet computation
 * 
 * @param fields the field name from the IndexSchema
 * @return this
 */
public SolrQuery addFacetField(String ... fields) {
  for( String f : fields ) {
    this.add(FacetParams.FACET_FIELD, f);
  }
  this.set(FacetParams.FACET, true);
  return this;
}

