private int getRIndex(StringBuffer sb, int start) {
  if (start == 0)
    start = 1;
  int i = start;
  for (; i < sb.length(); i++) {
    //first non-vowel preceded by a vowel
    if (!isVowel(sb.charAt(i)) && isVowel(sb.charAt(i - 1))) {
      return i + 1;
    }
  }
  return i + 1;
}

