public static int findIndex(int[] arr, int value, int low, int high) {
  // binary search
  while (low <= high) {
    int mid = (low+high) >>> 1;
    int found = arr[mid];

    if (found < value) {
      low = mid+1;
    }
    else if (found > value) {
      high = mid-1;
    }
    else {
      return mid;
    }
  }
  return low;
}

