// the little vint encoding used for var-deref
private static void writeVShort(IndexOutput o, int i) throws IOException {
  assert i >= 0 && i <= Short.MAX_VALUE;
  if (i < 128) {
    o.writeByte((byte)i);
  } else {
    o.writeByte((byte) (0x80 | (i >> 8)));
    o.writeByte((byte) (i & 0xff));
  }
}

