public void testMultiFields() throws Exception {
  MoreLikeThis mlt = new MoreLikeThis(reader);
  mlt.setAnalyzer(new MockAnalyzer(random, MockTokenizer.WHITESPACE, false));
  mlt.setMinDocFreq(1);
