// Remove the root Scorer from subScorers and re-establish it as a heap
private void heapRemoveRoot() {
    int size=subScorers.size();
    if (size==1)
        subScorers.remove(0);
    else {
        subScorers.set(0, subScorers.get(size-1));
        subScorers.remove(size-1);
        heapAdjust(0);
    }
}

