/**
 * Checks recursively if all subindexes are optimized 
 */
@Override
public boolean isOptimized() {
  for (final IndexReader reader : readers) {
    if (!reader.isOptimized()) {
      return false;
    }
  }

  // all subindexes are optimized
  return true;
}

