protected void doDelete(int n) throws CorruptIndexException, IOException {
  numDocs = -1;                             // invalidate cache
  int i = readerIndex(n);                   // find segment num
  subReaders[i].deleteDocument(n - starts[i]);      // dispatch to segment reader
  hasDeletions = true;
}

