private static GeoPoint getCenterOfMass(PlanetModel planetModel, List<GeoPoint> points) {
  double x = 0;
  double y = 0;
  double z = 0;
  //get center of mass
  for (GeoPoint point : points) {
    x += point.x;
    y += point.y;
    z += point.z;
  }
  return planetModel.createSurfacePoint(x / points.size(), y / points.size(), z / points.size());
}

