@Override
public Bounds getBounds(Bounds bounds) {
  bounds = super.getBounds(bounds);
  if (circlePlane == null) {
    // Entire world
    bounds.noTopLatitudeBound().noBottomLatitudeBound().noLongitudeBound();
    return bounds;
  }
  bounds.addPoint(center);
  circlePlane.recordBounds(planetModel, bounds);
  return bounds;
}

